INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (99, 3, '사기를 당했어요.', '중고 거래 중 사기 피해가 발생 시 상대 회원 검거 및 문제 상황 해결을 위해 빠른 시일 내 가까운 경찰서에 방문 또는 사이버 수사국에 신고 접수하여 법적으로 도움받아 보실 수 있기를 바라며 사이버 수사국 신고 접수 방법을 안내해 드립니다.', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (100, 3, '치즈마켓 사기판매 예방방법 !', '사기이력이 확인되지 않더라도 치즈마켓 사기 통합 조회 메뉴에서 조회 기능을 활용하여 추가 확인 해보세요. 가장 안전하게 거래를 하는 방법은 앱 내의 안전거래를 이용하는 것입니다.', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (101, 3, '거래/환불/합의/협의해야 하는데 상대방 연락이 안 와요.', '거래가 진행 중인 건과 관련한 계정 제한 상태에서는 일시적인 해제 및 채팅방 열람이 불가능하기에 아래 정보를 기재하여 앱 고객센터 1:1 문의로 남겨주시면 거래 중이시던 상대 회원님과 거래/환불/협의를 진행하실 수 있도록 다시 한번 회원님의 연락처를 전달해 드리도록 하겠습니다.', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
