INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (103, 3, '거래 중 이용제재 받아서 상대방과 연락할 수가 없어요.', '상대 회원의 연락처를 알지 못하신다면 진행 중인 거래 건에 관해 계정 제한 상태에서는 일시적인 해제 및 채팅방 열람이 불가능하기에 아래 정보를 기재하여 앱 고객센터 1:1문의로 남겨주시면 거래 중이시던 상대 회원님과 거래/환불/협의를 진행하실 수 있도록 회원님의 연락처를 전달해 드리도록 하겠습니다.', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (104, 3, '사기를 당한 것 같은데 상대방 연락처(전화번호)를 알고 싶어요.', '치즈마켓에서는 개인정보 보호법에 따라 회원님에게 직접적으로 상대 회원의 정보를 전달해 드릴 수는 없으나 상대 회원의 정보 제공 절차는 아래와 같습니다. 사기로 인한 피해가 발생한 경우 가까운 경찰서에 방문 또는 사이버 수사국에도 신고 접수하여 법적인 도움을 받아 보실 수 있기를 바라며 신고 접수 후 수사 중 필요한 정보가 있어 담당 수사관님이 공문 보내주실 때 내용 확인 후 조회할 수 있는 정보 제공하여 적극적으로 수사에 협조할 수 있도록 하겠습니다.', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (105, 3, '치즈마켓 거래 사기 대응 어떻게 하나요?', '치즈마켓 앱 이용 중 사기 피해 발생 시 추가적인 사기 피해 예방을 위해 사기 계정을 즉시 제재하고 있습니다.상대 회원 검거 및 문제 상황 해결을 위해 이른 시일 내 가까운 경찰서에 방문 또는 사이버 수사국에도 신고 접수하여 법적으로 도움받아 보실 수 있기를 바라며 사이버 수사국 신고 접수 방법을 안내해 드립니다.치즈마켓 앱에서 발생한 사건 관련 신고접수 이후 담당 수사관님이 치즈마켓으로 수사 협조 공문(압수수색 검증영장 등)을 보내주시는 경우 수사에 적극적으로 협조할 것을 약속드립니다.', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (106, 3, '신고 취소는 어떻게 하나요?', '신고 취소는 신고 접수한 회원님만 취소가 가능하며 고객센터 1:1문의로 접수해 주시면 검토 후 신고 철회 진행하겠습니다. ※ 접수 경로: 앱 고객센터 1:1문의 → 문의유형: 신고접수 → 상세유형: 사기의심신고', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (107, 3, '거래 중에 상대방이 탈퇴했어요.', '상대 회원과의 거래 중 사기 피해가 발생하였다면 치즈마켓 앱 내 채팅 내역(외부 채팅 유도 등)의 사기 정황을 확인할 수 있는 모든 내용 화면을 캡처하여 앱 고객센터 1:1 문의로 남겨주시면 내용 검토 후 개인정보 처리 방침에 따라 6개월간 해당 회원의 개인정보 보관이 진행될 예정이며 모든 사기 피해 자료를 모아 아래 사이버 수사국으로 신고 접수를 부탁드립니다.', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (108, 3, '피해자와 합의하고 싶어요.', '상대 회원의 신고 접수 사유로 서비스 이용 제재된 경우 해당 제재 해제를 위해서는 상대 회원과 거래/환불/합의 완료된 후 상대 회원이 치즈마켓 앱 내 1:1문의를 통해 신고 취소 요청을 남겨주셔야 계정 해제가 가능합니다.', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (109, 3, '사기 피해 시 어떻게 대처해야 하나요?', '사기 피해 발생 시 사기를 판단할 수 있는 증빙자료를 사기 신고를 통해 접수해 주시면 사기 피해 정황이 확인될 경우 해당 회원의 서비스 이용 제한을 진행하고 있습니다. 사기 피해로 판단하기에 증빙 자료가 부족할 경우 자료를 다시 요청드릴 수 있는 점 유의해주시길 바랍니다.', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (110, 3, '상대방이 제 개인정보를 과도하게 요구합니다.', '최근 치즈마켓에서 거래 중 상대 회원의 개인정보를 도용하여 발생하는 피해 사례가 다수 확인되고 있습니다. 민감 개인정보(인증번호신분증….)추가 입금을 요구하는 경우 절대 대응하지 마시고 소중한 회원님의 개인정보 보호를 위하여 치즈마켓 안전 거래를 이용해 보시기 바랍니다.', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
