INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (115, 4, '도용을 당한 것 같아요!', '내가 등록하지 않은 상품이 등록되어 있거나 다른 사유로 도용 의심될 경우 아래의 방법을 통해 피해 예방이 가능합니다. 1. 네이버 비밀번호 변경 및 네이버 2단계 인증 2. 치즈마켓 앱 > 마이 > 설정 > 회원정보 설정 > 하단 로그아웃 ', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
