INSERT INTO qnalist_board (no, que_i_cd, title, contents, ad_id, ad_name, r_date, m_date, view_cnt, state, first_date, first_id, last_date, last_id) VALUES (127, 5, '앱은 제재가 풀렸는데 카페는 언제 풀리나요?', '치즈마켓 앱 내 사기/아이디 도용 정황이 의심되어 서비스 이용이 제한되었다면 혹여라도 카페까지 발생할 수 있는 추가 피해 예방을 위해 카페 계정 또한 서비스 이용 제재된 것으로 보입니다.앱 계정 이용 제재 해제된 상태로 치즈마켓 카페 계정도 해제를 원하시는 경우 앱 고객센터 1:1문의로 문의 남겨주시면 회원님 정보 확인 후 해제 도와드리도록 하겠습니다.', 'asdf', '홍길동', '2024-05-11 23:20:43', '2024-05-11 23:20:43', 0, 'Y', '2024-05-11 23:20:43', 'asdf', now(), 'asdf');
